INSERT INTO `news` VALUES (121777,'Container chỏ sắt bị dập đầu xe do sắt dồn ','TTO - Ngày 28-6, tại ngã 3 Tân Cang, đoạn giao giữa quốc lộ 51 với đường Đinh Quang Ân (xã Phước Tân, TP. Biên Hòa, Đồng Nai) xảy ra một vụ tai nạn giao thông khiến xe container dập đầu, lơ xe bị thương.','http://tuoitre.vn/tin/chinh-tri-xa-hoi/20150628/hang-chuc-tan-sat-day-toi-truoc-xe-container-dap-dau/768228.html','tuoitre.vn','http://static.new.tuoitre.vn/tto/i/s490/2015/06/28/container-dap-dau-1-1435474094.jpg',0,'TINTRONGNUOC',1,'M','20150628 15:04'),(121778,'Nước mắt trẻ mồ côi, bất hạnh trong “ngôi nhà hạnh phúc”','','http://tuoitre.vn/tin/chinh-tri-xa-hoi/20150628/nuoc-mat-tre-mo-coi-bat-hanh-trong-ngoi-nha-hanh-phuc/768078.html','tuoitre.vn','http://static.new.tuoitre.vn/tto/i/s440/2015/06/28/5a039a53.jpg',0,'TINTRONGNUOC',0,'HI','20150628 15:04'),(121779,'Từ mạng xã hội ảo đến bi kịch thật: có cần khuôn khổ?','','http://tuoitre.vn/tin/chinh-tri-xa-hoi/20150628/tu-mang-xa-hoi-ao-den-bi-kich-that-co-can-khuon-kho/768045.html','tuoitre.vn','http://static.new.tuoitre.vn/tto/i/s440/2015/06/28/72ceb913-1435459325.jpg',0,'TINTRONGNUOC',0,'HI','20150628 15:04'),(121780,'Nguyễn Văn Linh - nhà lãnh đạo kiên định và sáng tạo','','http://tuoitre.vn/tin/chinh-tri-xa-hoi/20150628/nguyen-van-linh-nha-lanh-dao-kien-dinh-va-sang-tao/768054.html','tuoitre.vn','http://static.new.tuoitre.vn/tto/i/s440/2015/06/28/b0a736d7.jpg',0,'TINTRONGNUOC',0,'HI','20150628 15:04'),(121781,'Thông xe quốc lộ 14 Gia Lai - Đắk Nông','TTO - Sáng 28-6, Bộ GTVT phối hợp với Tập đoàn Đức Long Gia Lai (TP Pleiku, tỉnh Gia Lai) tổ chức lễ phát lệnh thông xe dự án đường Hồ Chí Minh đoạn qua tỉnh Gia Lai - Đắk Nông.','http://tuoitre.vn/tin/chinh-tri-xa-hoi/20150628/phat-lenh-thong-xe-quoc-lo-14-gia-lai-dak-nong/768234.html','tuoitre.vn','http://static.new.tuoitre.vn/tto/i/s146/2015/06/28/quoc-lo-14-1435474625.jpg',0,'TINTRONGNUOC',0,'HI','20150628 15:04'),(121782,'Gần 92.000 tỷ đồng xây dựng 3 tuyến metro TP.HCM','TTO 